Person {
    viewArguments: { readonly: true }

    navigateItems: [
        openView {
            title: 10
            arguments: {
                view: {
                    defaultRenderer: timeline

                    datasource {
                        query: "AuditItem appliesTo:{.id}"
                        sortProperty: dateCreated
                        sortAscending: true
                    }

                    [renderer = "timeline"] {
                        timeProperty: dateCreated
                    }
                }
            }
        }
        openViewByName {
            title: "{$starred} {type.plural()}"
            arguments: {
                name: "filter-starred"
                include: "all-{type}"
            }
        }
        openViewByName {
            title: "{$all} {type.lowercased().plural()}"
            arguments: {
                name: "all-{type}"
            }
        }
    ]
}
